const SearchTodos = () => {
  return (
    <div className="max-w-md mx-auto mt-8 p-6 bg-white rounded-lg shadow-lg">
      <h1 className="text-2xl font-bold text-gray-800 mb-6 text-center">
        Todo List
      </h1>

      {/* Input para agregar nuevos todos */}
      <div className="flex gap-2 mb-6">
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Agregar nueva tarea..."
          className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
        />
        <button
          onClick={addTodo}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
        >
          <Plus size={20} />
        </button>
      </div>

      {/* Lista de todos - estructura similar a dropdown-menu */}
      <ul className="bg-white border border-gray-200 rounded-lg shadow-sm overflow-hidden">
        {todos.map((todo, index) => (
          <React.Fragment key={todo.id}>
            <li className="group">
              <div className="flex items-center px-4 py-3 hover:bg-gray-50 transition-colors cursor-pointer">
                <button
                  onClick={() => toggleTodo(todo.id)}
                  className={`mr-3 w-5 h-5 rounded border-2 flex items-center justify-center transition-colors ${todo.completed
                    ? 'bg-green-500 border-green-500 text-white'
                    : 'border-gray-300 hover:border-green-400'
                    }`}
                >
                  {todo.completed && <Check size={14} />}
                </button>

                <span className={`flex-1 ${todo.completed
                  ? 'text-gray-500 line-through'
                  : 'text-gray-800'
                  }`}>
                  {todo.text}
                </span>

                <button
                  onClick={() => deleteTodo(todo.id)}
                  className="ml-3 p-1 text-gray-400 hover:text-red-500 transition-colors opacity-0 group-hover:opacity-100"
                >
                  <Trash2 size={16} />
                </button>
              </div>
            </li>

            {/* Divider - similar a dropdown-divider */}
            {index < todos.length - 1 && (
              <li>
                <hr className="border-t border-gray-200" />
              </li>
            )}
          </React.Fragment>
        ))}

        {todos.length === 0 && (
          <li className="px-4 py-8 text-center text-gray-500">
            No hay tareas pendientes
          </li>
        )}
      </ul>

      {/* Estadísticas */}
      <div className="mt-4 text-sm text-gray-600 text-center">
        Total: {todos.length} |
        Completadas: {todos.filter(todo => todo.completed).length} |
        Pendientes: {todos.filter(todo => !todo.completed).length}
      </div>
    </div>
  );

};
